import './post.css';
import { MoreVert } from '@mui/icons-material';
import { useState, useEffect } from 'react';
import axios from 'axios';
import { format } from 'timeago.js';
import Link from 'react-router-dom';

function Post({ post }) {
  const [like, setLike] = useState(post.likes.length);
  const [isLiked, setIsLiked] = useState(false);
  const [user, setUser] = useState({});

  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get(
        `http://localhost:8800/api/users/${post.userId}`
      );
      setUser(res.data);
    };
    fetchUser();
  }, [post.userId]);

  const PF = process.env.REACT_APP_PUBLIC_FOLDER;
  return (
    <div className="post">
      <div className="post-wrapper">
        <div className="post-top">
          <Link>
            <div className="post-top-left">
              <img
                src={user.profilePicture || PF + 'person/noAvatar.png'}
                alt=""
                className="post-profile-img"
              />
              <span className="post-profile-name">{user.username}</span>
              <span className="post-date">{format(post.createdAt)}</span>
            </div>
          </Link>

          <div className="post-top-right">
            <MoreVert className="post-settings" />
          </div>
        </div>
        <div className="post-center">
          <span className="post-text">{post.desc}</span>
          <img src={PF + post.img} alt="" className="post-img" />
        </div>
        <div className="post-bottom ">
          <div className="post-bottom-left">
            <img
              src={`${PF}like.png`}
              alt=""
              className="post-like-icon"
              onClick={() => {
                if (!isLiked) {
                  setLike(like + 1);
                  setIsLiked(true);
                } else {
                  setLike(like - 1);
                  setIsLiked(false);
                }
              }}
            />
            <span className="post-like-counter">{like} people have liked</span>
          </div>
          <div className="post-bottom-right">
            <span className="post-comment-text">{post.comment} Comments</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Post;
